package com.syntax.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1 Need to create a variable ( box)
		//2 need different names for the variable= Boxes
		/*to create a variable we need
		 * 1.- specify data type
		 * 2.- give a name
		 */
		//Data types to represent numeric values
		byte box1=127;
		short box2=32767;
		int box3=327677666;
		long box4=405712356778999L; 
		//Data types to represent decimals
		
		float price=12.99F;
		double price1=13.99; // most used
		// To represent a single character 
		
		char gender='F';
		
		char sign='$';
		
		//To represent yes or no
		
		boolean snow=false;
		boolean tired=true;
		
		// I want to create 13.99??
		
		System.out.println(price1);
		System.out.println(box1);
		System.out.println(box2);
		System.out.println(box3);
		System.out.println(box4);
		System.out.println(gender);
		System.out.println(price);
		System.out.println(sign);
		System.out.println(snow);
		System.out.println(tired);
		

	}

}
