package com.syntax.class07;

public class HomeWork1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1; i<=100; i++) {
        	System.out.print(i+" ");
		}
		
		System.out.println("100to1");
		
		for (int i=100; i>=1; i--) {
			System.out.print(i+" ");
		}

		System.out.println("even numbers from 20 to 1");
		
		for (int i=20;i>=2; i--) {
			System.out.print(i+" ");
			i -=1;
		}
		System.out.println("even numbers from 1 to 20");
		
		
		for (int i=2;i<=20;i++) {
			System.out.print(i+" ");
			i +=1;
		}
		
		System.out.println("odd numbers from 20 to 50");
		
		for (int i=21;i<=50;i++) {
			System.out.print(i+" ");
			i +=1;
		}
		System.out.println("odd numbers from 50 to 20");
		
		for (int i=49;i>=20;i--) {
			System.out.print(i+" ");
			i -=1;
		}
	
	
		
	}
	}
